import os, glob, re, shutil, zipfile, time

src_files = sorted(glob.glob("/mnt/data/A_*.png"), key=lambda p: os.path.getmtime(p))

def group_score(name):
    s = name.lower()
    # Group 1: Amman layover
    if any(k in s for k in ["amman", "travel_diary", "storyboard", "photo_diary", "photo_journal"]):
        return (1, s)
    # Group 2: Flight
    if "travelogue" in s or "two-panel_travelogue" in s:
        return (2, s)
    # Group 3: Arrival / rental / traffic
    if any(k in s for k in ["two-page_comic_strip", "two-page_digital_comic_strip", "journey", "traveler's_jo", "day_in_the_life"]):
        return (3, s)
    # Group 4: Foster Hack (semi-realist badge page)
    if "semi-realist" in s:
        return (4, s)
    # Group 5: Mariano's (we don't have explicit; fallback to travelogue_comic_layout_recounts_a_la as arrival to beach)
    if "travelogue_comic_layout_recounts_a_la" in s:
        return (5, s)
    # Group 6: Volleyball
    if any(k in s for k in ["beach_vol", "outd", "outdoor"]):
        return (6, s)
    # Group 7: Korean BBQ (two friends)
    if "two_frie" in s:
        return (7, s)
    # Group 8: XSport / LA Fitness / gym reset
    if any(k in s for k in ["xsport", "illustrates_a_nig"]):
        return (8, s)
    # Group 9: Night fit showcase
    if any(k in s for k in ["chicago_ni", "two-panel_digital_comic_illustration_in_a_semi-r", "digital_illustration_comic-style_illustration_ti"]):
        return (9, s)
    # Group 10: SpotHero garage gag (fallback to "details_a_traveler's_jo")
    if "details_a_traveler's_jo" in s:
        return (10, s)
    # Group 11: Chicago Pimpin encounter
    if any(k in s for k in ["chicago_pimpin", '"chica']):
        return (11, s)
    # Group 12: Celeste night page (6-panel depiction)
    if "six-panel_digital_illustration_comic_page_depict" in s:
        return (12, s)
    # Group 13: Self-Esteem Ledger (educational)
    if "educational_digital_comic_titled_\"the" in s:
        return (13, s)
    # Group 14: Hotel late night
    if "late-nigh" in s:
        return (14, s)
    # Default: put miscellaneous near the end but before hotel if likely general
    return (15, s)

scored = sorted([(group_score(os.path.basename(p)), p) for p in src_files], key=lambda x: x[0])

# Build folder with single root "2025-10-01"
target_root = "/mnt/data/2025-10-01"
if os.path.exists(target_root):
    # Clean existing files
    for f in glob.glob(os.path.join(target_root, "*")):
        try:
            os.remove(f)
        except Exception:
            pass
else:
    os.makedirs(target_root, exist_ok=True)

# Rename sequentially with leading zeros
for idx, ((grp, _), src) in enumerate(scored, start=1):
    dst_name = f"page-{idx:02d}.png"
    shutil.copy2(src, os.path.join(target_root, dst_name))

# Also choose a cover: pick the Amman page if exists, else first page
cover_src = None
for ((grp, _), src) in scored:
    if grp == 1: 
        cover_src = src
        break
if cover_src is None and scored:
    cover_src = scored[0][1]
if cover_src:
    shutil.copy2(cover_src, os.path.join(target_root, "cover.png"))

# Zip it
zip_path = "/mnt/data/chicago-ep1-2025-10-01.zip"
with zipfile.ZipFile(zip_path, 'w', zipfile.ZIP_DEFLATED) as zf:
    for name in sorted(os.listdir(target_root)):
        full = os.path.join(target_root, name)
        zf.write(full, os.path.join("2025-10-01", name))

len(scored), zip_path

